package com.xhn.entity;import javax.persistence.*;//标记此类为实体类@Entity//标记对应的数据库中的表@Table(name = "user")public class User {    //设置id为主键 自增    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)   private Integer id;   private String username;   private String password;   private String name;    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    @Override    public String toString() {        return "User{" +                "id=" + id +                ", username='" + username + '\'' +                ", password='" + password + '\'' +                ", name='" + name + '\'' +                '}';    }    public User(Integer id, String username, String password, String name) {        this.id = id;        this.username = username;        this.password = password;        this.name = name;    }    public User() {        super();    }}